package com.robben.bbs.config;import org.springframework.security.web.session.SessionInformationExpiredEvent;import org.springframework.security.web.session.SessionInformationExpiredStrategy;import org.springframework.stereotype.Component;import javax.servlet.http.HttpServletResponse;import java.io.IOException;import java.util.HashMap;//没见生效过，session无效都是MyInvalidSesssionStrategy起作用@Componentpublic class AjaxSessionInformationExpiredStrategy implements SessionInformationExpiredStrategy {    @Override    public void onExpiredSessionDetected(SessionInformationExpiredEvent event) throws IOException {        HttpServletResponse response = event.getResponse();        HashMap<String,String> hashMap=new HashMap();        hashMap.put("session过期了","请重新登录");        response.setContentType("application/json;charset=UTF-8");        response.setStatus(888);        response.getWriter().print(hashMap);        response.flushBuffer();    }}