package com.robben.bbs.config;import org.springframework.security.web.session.InvalidSessionStrategy;import javax.servlet.http.HttpServletRequest;import javax.servlet.http.HttpServletResponse;import java.io.IOException;//session过期策略public class MyInvalidSessionStrategy implements InvalidSessionStrategy {    @Override    public void onInvalidSessionDetected(HttpServletRequest request, HttpServletResponse response) throws IOException {        //  将Status Code 设为401，提示用户要登录        response.setStatus(401);        //一定要设置为UTF-8,不然会乱码        response.setContentType("application/json;charset=UTF-8");        response.getWriter().print("session无效，请重新登录");        //Response.Flush()的作用是将缓冲信息输出到页面        response.flushBuffer();    }}