package com.robben.bbs.controller;import com.robben.bbs.service.ActiveService;import com.robben.bbs.utilities.ResultInfo;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.web.bind.annotation.GetMapping;import org.springframework.web.bind.annotation.PathVariable;import org.springframework.web.bind.annotation.RestController;@RestControllerpublic class ActiveController {    private final ActiveService activeService;    @Autowired    public ActiveController(ActiveService activeService) {        this.activeService = activeService;    }    @GetMapping("/active/{userId}")    public ResultInfo getActive(@PathVariable("userId") long userId) {        return ResultInfo.success(activeService.getActive(userId));    }}