package com.robben.bbs.controller;import com.robben.bbs.pojo.Collection;import com.robben.bbs.service.CollectionService;import com.robben.bbs.utilities.ResultInfo;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.web.bind.annotation.*;@RestController@RequestMapping("/collection")public class CollectionController {    private CollectionService collectionService;    @Autowired    public CollectionController(CollectionService collectionService) {        this.collectionService = collectionService;    }    @PostMapping    public ResultInfo saveCollection(Collection collection) {        return ResultInfo.success(collectionService.saveCollection(collection));    }    @DeleteMapping    public ResultInfo deleteCollection(Long collectorId, Long postId) {        return ResultInfo.success(collectionService.deleteCollection(collectorId, postId));    }    @GetMapping    public ResultInfo getCollection(Long collectorId) {        return ResultInfo.success(collectionService.getCollection(collectorId));    }    @GetMapping("/postIdList")    public ResultInfo getCollectedPostIdList(Long collectorId) {        return ResultInfo.success(collectionService.getCollectedPostIdList(collectorId));    }}