package com.robben.bbs.controller;import com.robben.bbs.pojo.CommentReply;import com.robben.bbs.service.CommentReplyService;import com.robben.bbs.utilities.ResultInfo;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.web.bind.annotation.*;@RestController@RequestMapping("/commentReply")public class CommentReplyController {    private final CommentReplyService commentReplyService;    @Autowired    public CommentReplyController(CommentReplyService commentReplyService){        this.commentReplyService=commentReplyService;    }    @PostMapping    public ResultInfo saveCommentReply(@RequestBody CommentReply commentReply){        int result=commentReplyService.saveCommentReply(commentReply);        if(result!=0){            return ResultInfo.success();        }        return ResultInfo.fail("保存commentReply失败");    }    @DeleteMapping    public ResultInfo deleteCommentReplyById(Integer id){        int result=commentReplyService.deleteCommentReplyById(id);        if(result!=0){            return ResultInfo.success();        }        return ResultInfo.fail("删除失败");    }}