package com.robben.bbs.controller;import com.robben.bbs.service.ConfessionWallCommentService;import com.robben.bbs.utilities.ResultInfo;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.web.bind.annotation.DeleteMapping;import org.springframework.web.bind.annotation.PostMapping;import org.springframework.web.bind.annotation.RequestParam;import org.springframework.web.bind.annotation.RestController;import org.springframework.web.multipart.MultipartFile;import java.io.IOException;@RestControllerpublic class ConfessionWallCommentController {    private final ConfessionWallCommentService confessionWallCommentService;    @Autowired    public ConfessionWallCommentController(ConfessionWallCommentService confessionWallCommentService){        this.confessionWallCommentService=confessionWallCommentService;    }    @PostMapping("/confessionWallComment")    public ResultInfo saveConfessionWallComment(@RequestParam("picture") MultipartFile picture,                                         Integer confessionWallId,                                         Integer commenterId,                                         String content) throws IOException {        int result= confessionWallCommentService.saveConfessionWallComment(picture,                confessionWallId,commenterId, content);        if(result==1){            return ResultInfo.success();        }        return ResultInfo.fail("保存失败");    }    /**     *     * @param id     * @param pictureName     * @return     */    @DeleteMapping("/confessionWallComment")    public ResultInfo deleteConfessionWallCommentById(Integer id,                                                      String pictureName){        int result=                confessionWallCommentService.deleteConfessionWallCommentById(id,pictureName);        if(result!=0){            return ResultInfo.success();        }        return ResultInfo.fail("删除失败");    }}