package com.robben.bbs.controller;import com.robben.bbs.service.ConfessionWallCommentService;import com.robben.bbs.utilities.ResultInfo;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.web.bind.annotation.DeleteMapping;import org.springframework.web.bind.annotation.PostMapping;import org.springframework.web.bind.annotation.RequestParam;import org.springframework.web.bind.annotation.RestController;import org.springframework.web.multipart.MultipartFile;import java.io.IOException;@RestControllerpublic class ConfessionWallCommentController {    @Autowired    ConfessionWallCommentService confessionWallCommentService;    @PostMapping("/confessionWall/comment")    public ResultInfo savePictureComment(@RequestParam("picture") MultipartFile picture,                                         Integer confessionWallId,                                         Integer commenterId,                                         String commentContent) throws IOException {        int result= confessionWallCommentService.savePictureComment(picture,                confessionWallId,commenterId, commentContent);        if(result==1){            return ResultInfo.success();        }        return ResultInfo.fail("保存失败");    }    @DeleteMapping("/confessionWall/comment")    public ResultInfo deleteCommentById(Integer confessionWallCommentId,                                        String picturePath){        int result=confessionWallCommentService.deleteCommentById(confessionWallCommentId,picturePath);        if(result!=0){            return ResultInfo.success();        }        return ResultInfo.fail("删除失败");    }}