package com.robben.bbs.controller;import com.robben.bbs.pojo.ConfessionWall;import com.robben.bbs.service.ConfessionWallService;import com.robben.bbs.utilities.ResultInfo;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.web.bind.annotation.*;import org.springframework.web.multipart.MultipartFile;import java.io.IOException;@RestControllerpublic class ConfessionWallController {    @Autowired    ConfessionWallService confessionWallService;    /**     *  查看表白墙     * @return 所有表白墙的内容     */    @GetMapping("/confessionWall")    public ResultInfo getConfessionWall(){        return ResultInfo.success(confessionWallService.getAllConfessionWall());    }    /**     * 表白墙发帖子     * 接受多文件和userId，content参数，并将文件存入磁盘     * @param multipartFile 存放多个文件的数组     * @param userId     * @param content     * @return 成功则true     * @throws IOException     */    @PostMapping("/confessionWall")    public ResultInfo postConfessionWall(@RequestParam("file") MultipartFile[] multipartFile,                                      @RequestParam("userId") Integer userId,                                      @RequestParam("content") String content) throws IOException {        return ResultInfo.success(confessionWallService.postConfessionWall(userId,content,multipartFile));    }    /**     * 删除表白墙     * @param id 表白墙id     * @return     */    @DeleteMapping("/confessionWall")    public ResultInfo deleteConfessionWallById(@RequestBody Integer id){        return ResultInfo.success(confessionWallService.deleteConfessionWallById(id));    }    /**     * 修改表白墙内容     * @param content     * @return     */    @PatchMapping("/confessionWall")    public ResultInfo updateConfessionWall(@RequestBody String content){        return ResultInfo.success(confessionWallService.updateConfessionWall(content));    }}