package com.robben.bbs.controller;import com.robben.bbs.pojo.OrdinaryUser;import com.robben.bbs.service.OrdinaryUserService;import com.robben.bbs.utilities.ResultInfo;import org.springframework.web.bind.annotation.*;import org.springframework.web.multipart.MultipartFile;import java.io.IOException;@RestController@RequestMapping("/user")public class OrdinaryUserController {    private final OrdinaryUserService ordinaryUserService;    public OrdinaryUserController(OrdinaryUserService ordinaryUserService) {        this.ordinaryUserService = ordinaryUserService;    }    @PostMapping    public ResultInfo updateUser(MultipartFile avatarFile, OrdinaryUser ordinaryUser) throws IOException {        return ResultInfo.success(ordinaryUserService.updateUser(avatarFile, ordinaryUser));    }    @GetMapping("/{userId}")    public ResultInfo getUserInfo(@PathVariable("userId") Long userId) {        return ResultInfo.success(ordinaryUserService.getUserInfoById(userId));    }    @PutMapping("/register")    public ResultInfo saveUser(@RequestBody OrdinaryUser ordinaryUser) {        return ResultInfo.success(ordinaryUserService.saveUser(ordinaryUser));    }    @GetMapping("/userRole/{userId}")    public ResultInfo getUserRole(@PathVariable("userId") Long userId) {        Integer res = ordinaryUserService.getUserRole(userId);        if (res == 1) {            return ResultInfo.success("admin");        }        return ResultInfo.success("ordinaryUser");    }}