package com.robben.bbs.controller;import com.robben.bbs.service.OrdinaryUserService;import com.robben.bbs.utilities.ResultInfo;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.web.bind.annotation.GetMapping;import org.springframework.web.bind.annotation.PostMapping;import org.springframework.web.bind.annotation.RequestParam;import org.springframework.web.bind.annotation.RestController;import org.springframework.web.multipart.MultipartFile;import java.io.File;import java.io.IOException;@RestControllerpublic class OrdinaryUserController {    @Autowired    OrdinaryUserService ordinaryUserService;    /**     * 保存用户上传的头像     * @param multipartFile 用户上传的头像     * @param userId     * @throws IOException     */    @PostMapping("avatar")    public ResultInfo uploadUserAvatar(@RequestParam("avatar") MultipartFile multipartFile,                                       @RequestParam("userId") long userId           ) throws IOException {        String imageName=userId+multipartFile.getContentType().substring(6);        File file=new File("/Users/pengchunkao/userAvatar/"+imageName);        multipartFile.transferTo(file);        return ResultInfo.success();    }    @GetMapping("test")    public String test(String username){        ordinaryUserService.queryByUsername(username);        return "ss";    }}