package com.robben.bbs.controller;import com.robben.bbs.service.PostCommentService;import com.robben.bbs.utilities.ResultInfo;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.web.bind.annotation.DeleteMapping;import org.springframework.web.bind.annotation.PostMapping;import org.springframework.web.bind.annotation.RequestParam;import org.springframework.web.bind.annotation.RestController;import org.springframework.web.multipart.MultipartFile;import java.io.IOException;@RestControllerpublic class PostCommentController {    @Autowired    PostCommentService postCommentService;    /**     * 发表评论     * @param     * @return     */    @PostMapping("/postComment")    public ResultInfo saveComment(@RequestParam("picture") MultipartFile multipartFile,                                  Long postId, Integer commenterId,String content) throws IOException {        int res=postCommentService.savePostComment(multipartFile,postId,                commenterId,content);        if(res==1) {            return ResultInfo.success();        }        return ResultInfo.fail("保存失败");    }    /**     * 删除评论     * @param id     * @return     */    @DeleteMapping("/postComment")    public ResultInfo deletePostCommentById(Integer id,String pictureName){        int res=postCommentService.deletePostCommentById(id,pictureName);        if(res!=0) {            return ResultInfo.success();        }        return ResultInfo.fail("删除失败");    }}