package com.robben.bbs.controller;import com.robben.bbs.pojo.Comment;import com.robben.bbs.pojo.Post;import com.robben.bbs.service.PostService;import com.robben.bbs.utilities.ResultInfo;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.web.bind.annotation.PostMapping;import org.springframework.web.bind.annotation.RequestBody;import org.springframework.web.bind.annotation.RestController;@RestControllerpublic class PostController {    @Autowired    PostService postService;    /**     * 发布帖子     * @param post     * @return     */    @PostMapping("/post")    public Boolean postPost(@RequestBody Post post){        return postService.postPost(post);    }    /**     * 发表评论     * @param comment     * @return     */    @PostMapping("/comment")    public ResultInfo postComment(@RequestBody Comment comment){        return ResultInfo.success(postService.postComment(comment));    }}