package com.robben.bbs.controller;import com.robben.bbs.mapper.PostMapper;import com.robben.bbs.pojo.Comment;import com.robben.bbs.pojo.Post;import com.robben.bbs.service.PostService;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.web.bind.annotation.PostMapping;import org.springframework.web.bind.annotation.RequestBody;import org.springframework.web.bind.annotation.RestController;@RestControllerpublic class PostController {    @Autowired    PostService postService;    @PostMapping("/post")    public String postPost(@RequestBody Post post){        return postService.postPost(post);    }    @PostMapping("/comment")    public String postComment(@RequestBody Comment comment){        return postService.postComment(comment);    }}