package com.robben.bbs.controller;import com.robben.bbs.pojo.PrivateMessage;import com.robben.bbs.service.PrivateMessageService;import com.robben.bbs.utilities.ResultInfo;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.web.bind.annotation.GetMapping;import org.springframework.web.bind.annotation.PostMapping;import org.springframework.web.bind.annotation.RequestMapping;import org.springframework.web.bind.annotation.RestController;import java.util.List;@RestController@RequestMapping("/privateMessage")public class PrivateMessageController {    private final PrivateMessageService privateMessageService;    @Autowired    public PrivateMessageController(PrivateMessageService privateMessageService){        this.privateMessageService=privateMessageService;    }    @PostMapping    public Boolean savePrivateMessage(Integer senderId,String content,                                         Integer receiverId){        return privateMessageService.savePrivateMessage(senderId, content,                receiverId);    }    @GetMapping    public ResultInfo getAllPrivateMessage(Integer receiverId){        List<PrivateMessage> privateMessageList=                privateMessageService.getAllPrivateMessage(receiverId);        return ResultInfo.success(privateMessageList);    }}