package com.robben.bbs.controller;import com.robben.bbs.service.RelationshipService;import com.robben.bbs.utilities.ResultInfo;import org.springframework.web.bind.annotation.*;@RestController@RequestMapping("/relationship")public class RelationshipController {    private final RelationshipService relationshipService;    public RelationshipController(RelationshipService relationshipService){        this.relationshipService=relationshipService;    }    @GetMapping("/followers")    public ResultInfo getFollowerByUserId(Integer userId){        return  ResultInfo.success(relationshipService.getFollower(userId));    }    @GetMapping("/followees")    public ResultInfo getFolloweeByUserId(Integer userId){        return  ResultInfo.success(relationshipService.getFollowee(userId));    }    /**     *     * @param followeeId 被关注者Id     * @param followerId 关注者Id     * @return     */    @PostMapping("/follower")    public ResultInfo saveFollower(Integer followeeId,Integer followerId){        return ResultInfo.success(relationshipService.saveFollower(followeeId,followerId));    }    @DeleteMapping("/follower")    public  ResultInfo deleteFollower(Integer followeeId,Integer followerId){        return ResultInfo.success(relationshipService.deleteFollower(followeeId,followerId));    }}