package com.robben.bbs.controller;import com.robben.bbs.pojo.RichText;import com.robben.bbs.service.RichTextService;import com.robben.bbs.utilities.ResultInfo;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.web.bind.annotation.*;import java.util.HashMap;@RestController@RequestMapping("/richText")public class RichTextController {    private final RichTextService richTextService;    @Autowired    public RichTextController(RichTextService richTextService){        this.richTextService=richTextService;    }    @PostMapping    public ResultInfo  saveRichText(@RequestBody RichText richText){        System.out.println(richText.getAuthorId());        System.out.println(richText.getContent());        return ResultInfo.success(richTextService.saveRichText(richText.getAuthorId(),                richText.getContent()));    }    @GetMapping    public ResultInfo getRichTextById( Integer id){        return  ResultInfo.success(richTextService        .getRichTextById(id));    }}