package com.robben.bbs.controller;import com.robben.bbs.service.SearchService;import com.robben.bbs.utilities.ResultInfo;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.web.bind.annotation.GetMapping;import org.springframework.web.bind.annotation.RequestMapping;import org.springframework.web.bind.annotation.RestController;@RestController@RequestMapping("/search")public class SearchController {    private SearchService searchService;    @Autowired    public SearchController(SearchService searchService){        this.searchService=searchService;    }    @GetMapping    public ResultInfo search(String keyword){        return ResultInfo.success(searchService.query(keyword));    }}