package com.robben.bbs.controller;import com.robben.bbs.service.SquareCommentService;import com.robben.bbs.utilities.ResultInfo;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.web.bind.annotation.DeleteMapping;import org.springframework.web.bind.annotation.PostMapping;import org.springframework.web.bind.annotation.RestController;import java.io.IOException;@RestControllerpublic class SquareCommentController {    private final SquareCommentService squareCommentService;    @Autowired    public SquareCommentController(SquareCommentService squareCommentService) {        this.squareCommentService = squareCommentService;    }    @PostMapping("/squareComment")    public ResultInfo saveSquareComment(Long squareId, Long commenterId, String content) throws IOException {        int result = squareCommentService.saveSquareComment(squareId, commenterId, content);        if (result == 1) {            return ResultInfo.success();        }        return ResultInfo.fail("保存失败");    }    /**     * @param id     * @return     */    @DeleteMapping("/squareComment")    public ResultInfo deleteSquareCommentById(Integer id) {        int result =                squareCommentService.deleteSquareCommentById(id);        if (result != 0) {            return ResultInfo.success();        }        return ResultInfo.fail("删除失败");    }}