package com.robben.bbs.controller;import com.robben.bbs.service.TopicService;import com.robben.bbs.utilities.ResultInfo;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.web.bind.annotation.GetMapping;import org.springframework.web.bind.annotation.PathVariable;import org.springframework.web.bind.annotation.RequestMapping;import org.springframework.web.bind.annotation.RestController;@RestController@RequestMapping("/topic")public class TopicController {    private final TopicService topicService;    @Autowired    public TopicController(TopicService topicService){        this.topicService=topicService;    }    @GetMapping("/{type}")    public ResultInfo getTopic(@PathVariable("type") String type){        return ResultInfo.success(topicService.getTopic(type));    }}