package com.robben.bbs.mapper;import com.robben.bbs.pojo.OrdinaryUser;import org.apache.ibatis.annotations.Param;import org.springframework.security.core.GrantedAuthority;import org.springframework.stereotype.Repository;import java.util.HashMap;import java.util.List;@Repositorypublic interface OrdinaryUserMapper {    OrdinaryUser queryByUsername(@Param("username") String username);    Integer register(OrdinaryUser user);    GrantedAuthority getAuthoritiesByUserId(Integer userId);    HashMap getUserById(Integer id);    HashMap getUserIdAndAvatarByUsername(String username);}