package com.robben.bbs.mapper;import com.robben.bbs.pojo.Post;import org.springframework.stereotype.Repository;import java.util.List;@Repositorypublic interface PostMapper {    List getAllPosts();    Post getPostByPostId(Long id);    Integer savePost(Long id,Integer posterId,                     String content, String tittle, String topic);    Integer updatePost(Long postId,String topic,String tittle,String content,                       Integer readingTimes);    Integer deletePostById(Long id);}