package com.robben.bbs.service.impl;import com.robben.bbs.mapper.ActiveMapper;import com.robben.bbs.service.ActiveService;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.stereotype.Service;import java.util.HashMap;import java.util.List;@Servicepublic class ActiveServiceImpl implements ActiveService {    private final ActiveMapper activeMapper;    @Autowired    public ActiveServiceImpl(ActiveMapper activeMapper){        this.activeMapper=activeMapper;    }    @Override    public Integer saveActive(Long userId,String content,String action) {        return activeMapper.saveActive(userId, content, action);    }    @Override    public Integer deleteActive(Integer activeId) {        return activeMapper.deleteActive(activeId);    }    @Override    public List<HashMap> getActive(Long userId) {        return activeMapper.getActive(userId);    }}