package com.robben.bbs.service.impl;import com.robben.bbs.mapper.CollectionMapper;import com.robben.bbs.pojo.Collection;import com.robben.bbs.service.ActiveService;import com.robben.bbs.service.CollectionService;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.stereotype.Service;import java.util.List;@Servicepublic class CollectionServiceImpl  implements CollectionService {    private CollectionMapper collectionMapper;    private ActiveService activeService;    @Autowired    public CollectionServiceImpl(ActiveService activeService,CollectionMapper collectionMapper){        this.activeService=activeService;        this.collectionMapper=collectionMapper;    }    @Override    public Integer saveCollection(Collection collection) {        collectionMapper.saveCollection(collection);        return activeService.saveActive(collection.getCollectorId(),collection.getPostId(),"收藏了帖子");    }    @Override    public Integer deleteCollection(Long collectorId,Long postId) {        collectionMapper.deleteCollection(collectorId,postId);        return activeService.deleteActive(collectorId,postId,"收藏了帖子");    }    @Override    public List getCollection(Long collectorId) {        return collectionMapper.getCollection(collectorId);    }    @Override    public List getCollectedPostIdList(Long collectorId) {        return collectionMapper.getCollectedPostIdList(collectorId);    }}