package com.robben.bbs.service.impl;import com.robben.bbs.mapper.CommentReplyMapper;import com.robben.bbs.pojo.CommentReply;import com.robben.bbs.service.CommentReplyService;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.stereotype.Service;@Servicepublic class CommentReplyServiceImpl implements CommentReplyService {    private final CommentReplyMapper commentReplyMapper;    @Autowired    public CommentReplyServiceImpl(CommentReplyMapper commentReplyMapper){        this.commentReplyMapper=commentReplyMapper;    }    /**     * 保存commentReply     * @param commentReply     * @return     */    @Override    public Integer saveCommentReply(CommentReply commentReply) {        return commentReplyMapper.saveCommentReply(commentReply);    }    /**     * 删除commentReply     * @param id     * @return     */    @Override    public Integer deleteCommentReplyById(Integer id) {        return commentReplyMapper.deleteCommentReply(id);    }}