package com.robben.bbs.service.impl;import com.robben.bbs.mapper.ConfessionWallCommentMapper;import com.robben.bbs.pojo.ConfessionWallComment;import com.robben.bbs.service.ConfessionWallCommentService;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.stereotype.Service;import org.springframework.web.multipart.MultipartFile;import java.io.File;import java.io.IOException;import java.util.List;@Servicepublic class ConfessionWallCommentServiceImpl implements ConfessionWallCommentService {    private final ConfessionWallCommentMapper confessionWallCommentMapper;    @Autowired    public ConfessionWallCommentServiceImpl(ConfessionWallCommentMapper confessionWallCommentMapper){        this.confessionWallCommentMapper=confessionWallCommentMapper;    }    @Override    public Integer saveConfessionWallComment(Long confessionWallId, Long commenterId, String content) throws IOException {        return confessionWallCommentMapper.saveComment(confessionWallId,commenterId, content);    }    /**     * 删除图片后再删除记录     * @param id     * @return     */    @Override    public Integer deleteConfessionWallCommentById(Integer id) {        return confessionWallCommentMapper.deleteConfessionWallCommentById(id);    }    /**     * 根据confessionWallId删除评论和评论的图片     * @param confessionWallId     * @return     */    @Override    public Integer deleteConfessionWallCommentByWallId(Long confessionWallId) {        List<ConfessionWallComment> confessionWallCommentList= confessionWallCommentMapper.getConfessionWallCommentsByWallId(confessionWallId);       return confessionWallCommentMapper.deleteConfessionWallCommentByWallId(confessionWallId);    }}