package com.robben.bbs.service.impl;import com.robben.bbs.mapper.ConfessionWallMapper;import com.robben.bbs.pojo.ConfessionWall;import com.robben.bbs.service.ConfessionWallService;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.stereotype.Service;import java.util.List;@Servicepublic class ConfessionWallServiceImpl implements ConfessionWallService {    @Autowired    ConfessionWallMapper confessionWallMapper;    @Override    public List getConfessionWall() {        return confessionWallMapper.getConfessionWall();    }    @Override    public Boolean postConfessionWall(ConfessionWall confessionWall) {        return confessionWallMapper.postConfessionWall(confessionWall);    }}