package com.robben.bbs.service.impl;import com.robben.bbs.mapper.CronMapper;import com.robben.bbs.service.CronService;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.stereotype.Service;@Servicepublic class CronServiceImpl implements CronService {    private CronMapper cronMapper;    @Autowired    public CronServiceImpl(CronMapper cronMapper){        this.cronMapper=cronMapper;    }    @Override    public String getCron() {        return cronMapper.getCron();    }}