package com.robben.bbs.service.impl;import com.robben.bbs.mapper.PostMapper;import com.robben.bbs.pojo.Comment;import com.robben.bbs.pojo.Post;import com.robben.bbs.service.PostService;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.stereotype.Service;@Servicepublic class PostServiceImpl implements PostService {    @Autowired    PostMapper postMapper;    @Override    public Boolean postPost(Post post) {        return postMapper.postPost(post);    }    @Override    public Boolean postComment(Comment comment) {        return postMapper.postComment(comment);    }}