package com.robben.bbs.service.impl;import com.robben.bbs.mapper.PrivateMessageMapper;import com.robben.bbs.pojo.PrivateMessage;import com.robben.bbs.service.PrivateMessageService;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.stereotype.Service;import java.util.List;@Servicepublic class PrivateMessageServiceImpl implements PrivateMessageService {    private final PrivateMessageMapper privateMessageMapper;    @Autowired    public PrivateMessageServiceImpl(PrivateMessageMapper privateMessageMapper){        this.privateMessageMapper=privateMessageMapper;    }    @Override    public Integer savePrivateMessage(Integer senderId, String content, Integer receiverId) {        return privateMessageMapper.savePrivateMessage(senderId, content, receiverId);    }    @Override    public List<PrivateMessage> getAllPrivateMessage(Integer receiverId) {        return privateMessageMapper.getAllPrivateMessage(receiverId);    }}