package com.robben.bbs.service.impl;import com.robben.bbs.mapper.RelationshipMapper;import com.robben.bbs.service.RelationshipService;import org.springframework.stereotype.Service;import java.util.HashMap;import java.util.List;@Servicepublic class RelationshipServiceImpl implements RelationshipService {    private final RelationshipMapper relationshipMapper;    public RelationshipServiceImpl(RelationshipMapper relationshipMapper){        this.relationshipMapper=relationshipMapper;    }    @Override    public List<HashMap> getFollower(Integer followeeId) {        return relationshipMapper.getFollowerByUserId(followeeId);    }    @Override    public List<HashMap> getFollowee(Integer followerId) {        return relationshipMapper.getConcerningPeopleByUserId(followerId);    }    @Override    public Integer saveFollower(Integer followeeId, Integer followerId) {        return relationshipMapper.saveFollower(followeeId,followerId);    }    @Override    public Integer deleteFollower(Integer followeeId, Integer followerId) {        return null;    }}