package com.robben.bbs.service.impl;import com.robben.bbs.mapper.RelationshipMapper;import com.robben.bbs.service.ActiveService;import com.robben.bbs.service.RelationshipService;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.stereotype.Service;import java.util.HashMap;import java.util.List;@Servicepublic class RelationshipServiceImpl implements RelationshipService {    private final RelationshipMapper relationshipMapper;    private final ActiveService activeService;    @Autowired    public RelationshipServiceImpl(RelationshipMapper relationshipMapper, ActiveService activeService) {        this.relationshipMapper = relationshipMapper;        this.activeService = activeService;    }    @Override    public List<HashMap> getFollower(Long followeeId) {        return relationshipMapper.getFollower(followeeId);    }    @Override    public List<HashMap> getFollowee(Long followerId) {        return relationshipMapper.getFollowee(followerId);    }    @Override    public List<HashMap> saveFollower(Long followeeId, Long followerId) {        relationshipMapper.saveFollower(followeeId, followerId);        activeService.saveActive(followerId, followeeId, "关注了");        return relationshipMapper.getFollowee(followerId);    }    @Override    public List<HashMap> deleteFollower(Long followeeId, Long followerId) {        relationshipMapper.deleteFollower(followeeId, followerId);        activeService.deleteActive(followerId, followeeId, "关注了");        return relationshipMapper.getFollowee(followerId);    }}