package com.robben.bbs.service.impl;import com.robben.bbs.mapper.RichTextMapper;import com.robben.bbs.service.RichTextService;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.stereotype.Service;@Servicepublic class RichTextServiceImpl implements RichTextService {    private final RichTextMapper richTextMapper;    @Autowired    public RichTextServiceImpl(RichTextMapper richTextMapper){        this.richTextMapper=richTextMapper;    }    @Override    public Integer saveRichText(Integer authorId,String content) {        return richTextMapper.saveRichText(authorId,content);    }    @Override    public String getRichTextById(Integer id) {        return richTextMapper.getRichTextById(id);    }}