package com.robben.bbs.service.impl;import com.robben.bbs.mapper.OrdinaryUserMapper;import com.robben.bbs.mapper.PostMapper;import com.robben.bbs.service.SearchService;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.stereotype.Service;import java.util.HashMap;import java.util.List;@Servicepublic class SearchServiceImpl implements SearchService {    private OrdinaryUserMapper ordinaryUserMapper;    private PostMapper postMapper;    @Autowired    public SearchServiceImpl(OrdinaryUserMapper ordinaryUserMapper, PostMapper postMapper) {        this.postMapper = postMapper;        this.ordinaryUserMapper = ordinaryUserMapper;    }    @Override    public HashMap query(String keyword) {        HashMap<String, Object> result = new HashMap<>();        List user = ordinaryUserMapper.queryUser(keyword);        if (user.size() > 0) {            result.put("userList", user);        }        List post = postMapper.getPostByTitle(keyword);        if (post.size() > 0) {            result.put("postList", post);        }        return result;    }}