package com.robben.bbs.service.impl;import com.robben.bbs.mapper.SquareCommentMapper;import com.robben.bbs.pojo.SquareComment;import com.robben.bbs.service.SquareCommentService;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.stereotype.Service;import java.io.IOException;import java.util.List;@Servicepublic class SquareCommentServiceImpl implements SquareCommentService {    private final SquareCommentMapper squareCommentMapper;    @Autowired    public SquareCommentServiceImpl(SquareCommentMapper squareCommentMapper) {        this.squareCommentMapper = squareCommentMapper;    }    @Override    public Integer saveSquareComment(Long squareId, Long commenterId, String content) throws IOException {        return squareCommentMapper.saveComment(squareId, commenterId, content);    }    /**     * 删除图片后再删除记录     *     * @param id     * @return     */    @Override    public Integer deleteSquareCommentById(Integer id) {        return squareCommentMapper.deleteSquareCommentById(id);    }    /**     * 根据squareId删除评论和评论的图片     *     * @param squareId     */    @Override    public Integer deleteSquareCommentBySquareId(Long squareId) {        List<SquareComment> squareCommentList = squareCommentMapper.getSquareCommentsBySquareId(squareId);        return squareCommentMapper.deleteSquareCommentBySquareId(squareId);    }}