package com.robben.bbs.service.impl;import com.robben.bbs.mapper.TopicMapper;import com.robben.bbs.service.TopicService;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.stereotype.Service;import java.util.List;@Servicepublic class TopicServiceImpl implements TopicService {    private TopicMapper topicMapper;    @Autowired    public TopicServiceImpl(TopicMapper topicMapper) {        this.topicMapper = topicMapper;    }    @Override    public List getTopic(String type) {        return topicMapper.getTopic(type);    }}