package com.robben.bbs.utilities;import lombok.Data;@Datapublic class ResultInfo<T> {    private T data;    private Integer code;    private String msg;    public static <T> ResultInfo<T> success(T data) {        ResultInfo resultInfo = new ResultInfo();        resultInfo.code = ResultStatus.SUCCESS.getCode();        resultInfo.msg = ResultStatus.SUCCESS.getMsg();        resultInfo.data = data;        return resultInfo;    }    public static <T> ResultInfo<T> success() {        ResultInfo resultInfo = new ResultInfo();        resultInfo.code = ResultStatus.SUCCESS.getCode();        resultInfo.msg = ResultStatus.SUCCESS.getMsg();        return resultInfo;    }    public static <T> ResultInfo<T> fail(String msg) {        ResultInfo resultInfo = new ResultInfo();        resultInfo.code = ResultStatus.FAILED.getCode();        resultInfo.msg = msg;        return resultInfo;    }    public static <T> ResultInfo<T> fail() {        ResultInfo resultInfo = new ResultInfo();        resultInfo.code = ResultStatus.FAILED.getCode();        resultInfo.msg = ResultStatus.FAILED.getMsg();        return resultInfo;    }}